import { useState } from "react";
import { FaHome, FaPodcast, FaBriefcase, FaNewspaper, FaEnvelopeOpen, FaAdjust  } from "react-icons/fa";
export default function Nav(){
    //const [isTheme, SetTheme] = useState(false);
    const themeAdjust = (e) =>{
        document.body.classList.toggle("light-mode");
    }
    const  menuChange =(e) =>{
        document.querySelector(".active-btn").classList.remove("active-btn");
        this.classList.add("active-btn");
        document.querySelector(".active").classList.remove("active");
        document.getElementById(button.dataset.id).classList.add("active");
    }
    return(
        <>
        <div className="controls">
            <div className="control active-btn" data-id="home" onClick={menuChange} >
                <FaHome />
            </div>
            <div className="control" data-id="about">
                <FaPodcast />
            </div>
            <div className="control" data-id="portfolio">
                <FaBriefcase />
            </div>
            <div className="control" data-id="blogs">
                <FaNewspaper />
            </div>
            <div className="control" data-id="contact">
                <FaEnvelopeOpen />
            </div>
        </div>
        <div className="theme-btn"  onClick={themeAdjust}>
            <FaAdjust />
        </div>
        </>
    );
}
